import React from "react";
import Button from "./Button";
import Logo from "./pictures/logo3.png";
import Avatar from "./pictures/avatar2.png";
import {
    BellIcon,
    ChatIcon,
    ChevronDownIcon,
    LoginIcon,
    PlusIcon,
    SearchIcon,
    UserIcon,
} from "@heroicons/react/outline";
import { useState } from "react";

function Header() {
    //     const [userDropdownVisibilityClass, setuserDropdownVisibilityClass] = useState('hidden');

    //     function toggleUserDropdown() {
    //         if (userDropdownVisibilityClass === 'hidden') {
    //             setuserDropdownVisibilityClass('block');
    //         } else {
    //             setuserDropdownVisibilityClass('hidden')
    //         }
    //     }

    return (
        <header className="w-full bg-rd p-2">
            <div className="mx-4 flex relative">
                <img src={Logo} alt="" className="w-10 h-10 mr-4" />
                <form
                    action=""
                    className="bg-rd-brighter p-1 px-3 flex rounded-md border border-rb mx-4 flex-grow"
                >
                    <SearchIcon className="text-gray-300 h-8 w-8 mt-1" />
                    <input
                        type="text"
                        className="bg-rd-brighter text-sm p-1 pl-2 pr-0 block focus:outline-none text-white"
                        placeholder="Search"
                    />
                </form>
                {/* <button className='px-3 py-1'>
            <ChatIcon className='text-gray-400 w-8 h-8 m-1 mx-2'/>
        </button>
        <button className='px-3 py-1'>
            <BellIcon className='text-gray-400 w-8 h-8 m-1 mx-2' />  
        </button>
        <button className='px-3 py-1'>
            <PlusIcon className='text-gray-400 w-8 h-8 m-1 mx-2' />
        </button> */}

                <div className="mx-1">
                    <Button outlined className="ml-2 mr-1">
                        Login
                    </Button>
                    <Button className="mr-2">Signup</Button>
                </div>

                {/* <button className="rounded-md flex ml-4 border border-gray-700" onClick={() => toggleUserDropdown()}> */}

                {/* <!-- This example requires Tailwind CSS v2.0+ --> */}
                <div class="relative inline-block text-left">
                    <div>
                        <button type="button" class="inline-flex justify-center w-full rounded-md border border-gray-700 shadow-sm px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-100 focus:ring-indigo-500" id="menu-button" aria-expanded="true" aria-haspopup="true">
                            <UserIcon className="w-8 h-8 text-gray-400 m-1" />
                            {/* <!-- Heroicon name: solid/chevron-down --> */}
                            <svg class="-mr-1 ml-2 h-8 w-8" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                                <path fill-rule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clip-rule="evenodd" />
                            </svg>
                        </button>
                    </div>

                    {/* <!--
                    Dropdown menu, show/hide based on menu state.

                    Entering: "transition ease-out duration-100"
                    From: "transform opacity-0 scale-95"
                    To: "transform opacity-100 scale-100"
                    Leaving: "transition ease-in duration-75"
                    From: "transform opacity-100 scale-100"
                    To: "transform opacity-0 scale-95"
  --> */}
                    <div class="origin-top-right absolute right-0 mt-2 w-56 rounded-md shadow-lg bg-white ring-1 ring-black ring-opacity-5 focus:outline-none" role="menu" aria-orientation="vertical" aria-labelledby="menu-button" tabindex="-1">
                        <div class="py-1" role="none">
                            {/* <!-- Active: "bg-gray-100 text-gray-900", Not Active: "text-gray-700" --> */}
                            <a href="#" class="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-0">Account settings</a>
                            <a href="#" class="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-1">Support</a>
                            <a href="#" class="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-2">License</a>
                            <form method="POST" action="#" role="none">
                                <button type="submit" class="text-gray-700 block w-full text-left px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-3">Sign out</button>
                            </form>
                        </div>
                    </div>
                </div>

                {/* <button id="dropdownDefault" data-dropdown-toggle="dropdown" className="rounded-md flex ml-4 border border-gray-700" type="button">
                    <UserIcon className="w-8 h-8 text-gray-400 m-1" /> */}
                {/* <div className="bg-gray-800 rounded-md w-10 h-10">
            <img src={Avatar} alt="" />
          </div> */}
                {/* <ChevronDownIcon className="text-gray-500 w-8 h-8 mt-2 ml-1" /> */}
                {/* <svg class="ml-2 w-4 h-4" aria-hidden="true" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M19 9l-7 7-7-7"></path></svg></button>


                <div id="dropdown" class="hidden z-10 w-44 bg-white rounded divide-y divide-gray-100 shadow dark:bg-gray-700">
                    <ul class="py-1 text-sm text-gray-700 dark:text-gray-200" aria-labelledby="dropdownDefault">
                        <li>
                            <a href="#" class="block py-2 px-4 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Dashboard</a>
                        </li>
                    </ul>
                </div>


                <div className={"absolute right-0 top-12 bg-rd border border-gray-700 z-10 rounded-md text-rt overflow-hidden"}>
                    <button
                        className="block felx w-50 py-2 px-3 hover:bg-gray-300  hover:text-black text-sm">
                        <LoginIcon className="w-6 h-6 " />
                        Login / Signup
                    </button>
                </div> */}
            </div>
        </header >
    );
}

export default Header;
