import React from "react";
import { Link } from "react-router-dom";
import PostContent from "./PostContent";

function Showposts(props) {

  let postClasses = "block border rounded-md " + (props.open ? "" : "hover:border-rt cursor-pointer");
  if (props.isListing) {
    postClasses += " bg-rd-lowbright p-3 mx-6 border-2 border-reddit_border";
  } else {
    postClasses += " border-none";
  }

  return (
    <div>
      <div className="px-6 text-rt pb-4">
        <Link to={'/comments/' + props._id} className="block border border-rb bg-rd-lowbright p-2 rounded-md hover:border-rt cursor-pointer">
          <h5 className="text-rt-darker text-sm mb-1">
            Posted by u/{props.author} 5 hours ago: {props.postedAt}
          </h5>
          <h2 className="text-2xl mb-3">{props.title}</h2>
          <div className="text-sm leading-6">
            {props.body}
          </div>
        </Link>
      </div>
    </div>
  );
}

export default Showposts;
