import React from 'react'
//import PostContent from "./PostContent";
import { useEffect, useState } from "react";
import axios from "axios";
import ClickOutHandler from 'react-clickout-handler';
// import CommentForm from "./CommentForm";
// import Comments from "./Comments";
// import RootCommentContext from "./RootCommentContext";
import Comment from "./Comment";

function CommentModal(props) {

    const [comment, setComment] = useState({});

    const visibleClass = props.open ? 'block' : 'hidden';

    useEffect(() => {
        axios.get('http://localhost:4000/comments/' + props.id)
            .then(response => {
                setComment(response.data);
            });
    }, [props.id]);

    function close() {
        setComment({});
        props.onClickOut();
    }

    return (
        <div className={"w-screen h-screen fixed top-0 left-0 z-20 flex block"} style={{ backgroundColor: 'rgba(0,0,0,.8)' }}>
            <div className="block overflow-scroll">
                <ClickOutHandler onClickOut={() => close()}>
                    <div className="border my-4 border-rd-brighter w-3/4 lg:w-1/2 bg-rd-lowbright text-rt self-center p-4 mx-auto rounded-md">
                        <div className="">
                            <Comment comment={comment} id={props.id} />
                        </div>
                    </div>
                </ClickOutHandler>
            </div>
        </div>
    );
}

export default CommentModal;
