import React from 'react'
import Input from "./Input";
import Button from "./Button";
import { useState, useContext } from 'react';

function Authentication() {

    const [modalType, setModalType] = useState('login');
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');


    function register(e) {

    }
    return (
        <div className={"w-screen h-screen fixed top-0 left-0 z-30 flex "} style={{ backgroundColor: 'rgba(0,0,0,.6)' }}>
            <div className="border border-rb w-3/4 sm:w-1/2 lg:w-1/4 bg-rd p-5 text-rt self-center mx-auto rounded-md">
                {modalType === 'login' && (
                    <h1 className="text-2xl mb-5">Login</h1>
                )}
                {modalType === 'register' && (
                    <h1 className="text-2xl mb-5">Sign Up</h1>
                )}

                {modalType === 'register' && (
                    <label>
                        <span className="text-rt-darker text-sm">E-mail:</span>
                        <Input type="text" className="mb-3 w-full" value={email} onChange={e => setEmail(e.target.value)} />
                    </label>
                )}

                <label>
                    <span className="text-rt-darker text-sm">Username:</span>
                    <Input type="text" className="mb-3 w-full" value={username} onChange={e => setUsername(e.target.value)} />
                </label>

                <label>
                    <span className="text-rt-darker text-sm">Password:</span>
                    <Input type="text" className="mb-3 w-full" value={password} onChange={e => setPassword(e.target.value)} />
                </label>

                {modalType === 'login' && (
                    <Button className="w-full py-2 mb-3" style={{ borderRadius: '.3rem' }} onClick={() => login()}>
                        Log In
                    </Button>
                )}
                {modalType === 'register' && (
                    <Button className="w-full py-2 mb-3" style={{ borderRadius: '.3rem' }} onClick={e => register(e)}>
                        Sign Up
                    </Button>
                )}
                {modalType === 'login' && (
                    <div>
                        New to the Community? <button className="text-blue-600" onClick={() => setModalType('register')} > SIGN UP </button>
                    </div>
                )}
                {modalType === 'register' && (
                    <div>
                        New to the Community? <button className="text-blue-600" onClick={() => setModalType('login')} > Login </button>
                    </div>
                )}
            </div>
        </div >
    );
}

export default Authentication
